svg.script:
	var animation = (function() {
		var pulsar = document.getElementById("pulsar");
		var echo = document.getElementById("echo");

		var pulsarRadius = +pulsar.getAttribute("r");
		var maxRadius = 20;
		var minRadius = 10;
		var echoRadius = +echo.getAttribute("r");

		var fps = 60;    //Frames Per Second
		var distancePerSecond = 1.2; //velocity of eyelids moving
		var distancePerFrame =  distancePerSecond / fps;
		var timer = null;

		var setTimer = function() {
			timer = setInterval(pulse, 1000 / fps);
		};

		var pulse = function() {
			if (pulsarRadius < minRadius || pulsarRadius > maxRadius) distancePerFrame *= -1;
			pulsarRadius += distancePerFrame;
			pulsar.setAttributeNS(null, "r", pulsarRadius);
			echoRadius -= distancePerFrame * 2;
			echo.setAttributeNS(null, "r", echoRadius);
		};

		return {
			run: setTimer
		}
	})();
